import React,{useState} from 'react'
import "./Dashboard.css"
import ContentArea from './ContentArea'
import LogoImg from "../assets/logo-icon.svg"
import DashboardActiveIcon  from "../assets/dashboard-active.svg"
import GraphIcon from "../assets/graph.svg"
import AppointmentIcon from "../assets/appointments.svg"
import EnquiresIcon  from "../assets/enquiries.svg"
import MessagesIcon from "../assets/messages.svg"
import UsersIcon from "../assets/users.svg"
import SettingsIcon from "../assets/settings.svg"
import logoutIcon from "../assets/logout.svg"


function LeftNav() {
  const [toggle,setToggle] = useState("")
  const [dashboard,setDashboard] = useState("")
  const [graph,setGraph] = useState("")
   const toggleClass = (prev)=>{
    setToggle((prev) => {
      if (prev == "") {
          return "activeicon"
      }
      else {
          return "";
      }
  })

  }


  const dashBoardContent =(pre)=>{
    setDashboard((pre) => {
      if (pre == "") {
          return "activenav"
      }
      else {
          return "";
      }
  })
  }


  const graphContent =(pre)=>{
    setGraph((pre) => {
      if (pre == "") {
          return "activenav"
      }
      else {
          return "";
      }
  })
  setDashboard("")
  }
  return (
    <>
    <div class="sidebar-left d-flex text-center py-20 flex-direction-column justify-content-between">
      <span className='toggle-icon d-none' onClick={toggleClass}></span>
    <a class="navbar-brand"><img src={LogoImg} alt="Logo" /></a>

     <nav class={`d-flex flex-direction-column ${toggle}`}>
        <a href="#first" className={`active ${dashboard}`}><img src={DashboardActiveIcon} alt="" onClick={dashBoardContent} className='icon '/></a>
        <a href="#first"><img src={GraphIcon} alt="" onClick={graphContent}/></a>
        <a href="#first"><img src={AppointmentIcon} alt=""/></a>
        <a href="#first"><img src={EnquiresIcon} alt=""/></a>
        <a href="#first"><img src={MessagesIcon} alt=""/></a>
        <a href="#first"><img src={UsersIcon} alt=""/></a>
        <a href="#first"><img src={SettingsIcon} alt=""/></a>
    </nav>

    <a class="logout"><img src={logoutIcon} alt="Logout" /></a>

</div>

<ContentArea />
</>


  )
}

export default LeftNav
